mapper.select:
select *,(select max(reviewtime) from postcomment p where p.postId=po.postId)lasttime from post po where fmid in (select fmid from forummanagement where pid=#{pid})
and title like '%${title}%'
and fmid = #{forumid}
and essence = #{essence}
order by solid desc,releasetime desc
order by solid desc,essence desc,lasttime desc,releasetime desc

select * from postcomment
where postId = #{postId}
order by reviewtime desc

SELECT *,(SELECT MAX(reviewtime) FROM postcomment p WHERE p.postId=po.postId)lasttime FROM post po WHERE userId = #{userId}
and title like '%${title}%'
ORDER BY releasetime DESC

mapper.insert:
insert into
postfabulous(`userID`,`postID`,`fabulousTime`)
values(#{fabu.userID},#{fabu.postID},#{fabu.fabulousTime});
update post
set fabulousNumber = fabulousNumber+1
where postId = #{fabu.postID}

mapper.select:
select count(1) from
postcollection
where pcId = #{postId} and userId = #{userId}

mapper.insert:
insert postcollection (pcID,userID,collectionTime)
value(#{coll.pcID},#{coll.userID},#{coll.collectionTime});
update post 
set collectionNumber=collectionNumber+1
where postId=#{coll.pcID}
